import React from "react";
import ReactDOM from "react-dom/client";
import App from "./App";
import "./index.css";

// ðŸ”¹ Filtro para ignorar errores de extensiones como MetaMask en desarrollo
if (process.env.NODE_ENV === "development") {
  window.addEventListener("error", (e) => {
    const fromExtension = String(e?.filename || "").startsWith("chrome-extension://");
    const isMetamask = /metamask/i.test(String(e?.message || ""));
    if (fromExtension || isMetamask) {
      e.preventDefault();
    }
  });

  window.addEventListener("unhandledrejection", (e) => {
    const msg = String((e as PromiseRejectionEvent)?.reason?.message || (e as any)?.reason || "");
    if (/metamask/i.test(msg)) {
      e.preventDefault();
    }
  });
}

// ðŸ”¹ Montar React normalmente
const root = ReactDOM.createRoot(document.getElementById("root") as HTMLElement);
root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);
